/**
 * Adds a point to the point centroid accumulator.
 * @param {jsts.geom.Coordinate} pt
 * @private
 */
jsts.algorithm.Centroid.prototype.addPoint = function (pt) {
    this.ptCount += 1;
    this.ptCentSum.x += pt.x;
    this.ptCentSum.y += pt.y;
};
