jsts.operation.buffer.SubgraphDepthLocater.DepthSegment.prototype.compareX = function(
    seg0, seg1) {
  var compare0 = seg0.p0.compareTo(seg1.p0);
  if (compare0 !== 0)
    return compare0;
  return seg0.p1.compareTo(seg1.p1);

};
