        if (paraC > maxPara) maxPara = paraC;
        if (paraC < minPara) minPara = paraC;

        var perpC = jsts.algorithm.MinimumDiameter.computeC(-dy, dx, this.convexHullPts[i]);
        if (perpC > maxPerp) maxPerp = perpC;
        if (perpC < minPerp) minPerp = perpC;
