/**
 * @private
 */
jsts.algorithm.CentralEndpointIntersector.prototype.compute = function() {
  var centroid = jsts.algorithm.CentralEndpointIntersector.average(this.pts);
  this.intPt = this.findNearestPoint(centroid, this.pts);
};


